stop = False
client = Client('startscripts', '26817926', '170e3ff3845374cf26f0566f7423056f')
print(Fore.RED + '\n█▀ ▀█▀ ▄▀█ █▀█ ▀█▀\n▄█ ░█░ █▀█ █▀▄ ░█░\n\n█▀ █▀▀ █▀█ █ █▀█ ▀█▀ █▀\n▄█ █▄▄ █▀▄ █ █▀▀ ░█░ ▄█' + Style.RESET_ALL)

client.start()
client.stop()

@client.on_message(filters.command('flood', prefixes='.') & filters.me)
def message_handler(client, message):
    global stop
    args = message.text.split(' ')
    if args[1] == 'stop':
        stop = True
        client.send_message(message.chat.id, 'flood stopped 🧨')
    i = int(args[1])
    while i >= 0:
        try:
            if(stop == True):
                i = 0
                stop = False
            client.send_message(message.chat.id, args[2])
            sleep(1/7)
        except FloodWait as e:
            sleep(e.x)
        i -= 1
        
@client.on_message(filters.command("gifsend", prefixes=".") & filters.me)
def sendgif(app, message):
	for _ in range(int(message.command[1])):
		sleep(0.01)
		app.send_document(message.chat.id, "https://tenor.com/ru/view/happy-llamas-gif-13768958")
		
@client.on_message(filters.command('reaction', prefixes='.') & filters.me)
async def unpin(app, msg):
	emoji = ' '.join(msg.command[1:])
	try:
		await app.send_reaction(chat_id=msg.chat.id, message_id=msg.reply_to_message_id, emoji=emoji)
		await msg.delete()
	except Exception as e:
		await msg.edit('mistake for you 👻')
		
@client.on_message(filters.command("try", prefixes=".") & filters.me)
def betaloves(_, msg):
	t = ["[Удачно]", "[Не удачно]"]

	try0 = random.choice(t)
	try1 = " ".join(msg.command[1:])

	if not try1:
		msg.edit(f'''
			**mistake for you 👻**''')
		sleep(1.5)
		msg.delete()
	else:
		msg.edit(f'''
			{try1} {try0}''')

