@client.on_message(filters.command('world', prefixes='.') & filters.me)
async def world(app, msg):
	text = ' '.join(msg.command[1:])
	for i in range(5):
		await msg.edit('🌎')
		sleep(0.4)
		await msg.edit('🌍')
		sleep(0.4)
		await msg.edit('🌏')
		sleep(0.3)
	try:
		await msg.edit(f'{text}')
	except Exception as e:
		await msg.edit('mistake for you 👻')
		print(e)
		sleep(2)
		await msg.delete()
		
